    [<Fact>]
    let ``returns same sequence of int64 as JDK8``() =
        let r = ref (Random.CreateWithSeed 25122001UL)
        let longs = [| for i in 1..100 -> Random.NextInt64 r |]
        test <@ Array.zip jdk8int64 longs |> Array.forall(fun (l,r) -> l = r) @>

