    [<Fact>]
    let ``returns same sequence of float64 as JDK8``() =
        let r = ref (Random.CreateWithSeed 25122001UL)
        let floats = [| for i in 1..100 -> Random.NextFloat r |]
        test <@ Array.zip jdk8float64 floats |> Array.forall(fun (l,r) -> float l = r) @>

